node.browser_test:
var soda = require('soda')
  , assert = require('assert');

var browser = soda.createClient({
    host: 'localhost'
  , port: 4444
  , url: 'http://localhost:5984/'
  , browser: 'firefox'
});

browser.on('command', function(cmd, args){
  console.log(' \x1b[33m%s\x1b[0m: %s', cmd, args.join(', '));
});

browser
  .chain
  .session()
  .open('/pages/_design/pages/_rewrite/page/index')
  .getTitle(function(title){
    assert.ok(~title.indexOf('Pages'), 'Title did not match, was '+title);
  })
  .testComplete()
  .end(function(err){
    if (err) {
      console.log('err', err)
      // throw err;
    }
    console.log('done');
  });

templates.page:
{{>header}}
    <div id="tools"></div>
    <div id="wiki"><div class="wiki">{{{body}}}</div></div>
    <div id="comments"></div>
    {{#has_atts}}
    <div id="files">
      <p>Files attached to <em>{{title}}</em>:</p>
      <ul>
        {{#atts}}
          <li><a href="{{uri}}">{{name}}</a> ({{type}})</li>
        {{/atts}}
      </ul>
    </div>
    {{/has_atts}}
  </body>
  <script src="../script/myloader.js"></script>
  <script src="/_utils/script/base64.js"></script>
  <script src="/_utils/script/jquery.form.js"></script>
  <script type="text/javascript" charset="utf-8">
    var opts = {};
    if (document.location.pathname.indexOf("_design") == -1) {
      // we are in a vhost
      opts.db = "pages";
      opts.design = "pages";
    };
    opts.ddoc = {{{ddoc}}};
    $.couch.app(function(app) {
      $("#account").evently("account", app);
      $("#profile").evently("profile", app);
      $.evently.connect("#account","#profile", ["loggedIn","loggedOut"]);
      $$("#wiki").docid = {{docid}};
      $$("#wiki").title = {{title_json}};
      $("#wiki").evently("wiki", app);
      $("#comments").evently("comments", app);
      $("#tools").evently("tools", app);
      $.pathbinder.begin("{{begin}}");
    }, opts);
  </script>
</html>

